# Perform paired t-test and compute confidence interval for each combination
datasets = [(ann_data, lr_data), (base_data, ann_data), (base_data, lr_data)]


for idx, (data1, data2) in enumerate(datasets, start=1):
    print(f"Paired t-test {idx}:")
    t_statistic, p_value = stats.ttest_rel(data1, data2)
    print("t-statistic:", t_statistic)
    print("p-value:", p_value)
    
    print(f"Confidence interval {idx}:")
    differences = data1 - data2
    confidence_interval = stats.t.interval(0.95, len(differences)-1, loc=np.mean(differences), scale=stats.sem(differences))
    print("95% confidence interval for the mean difference:", confidence_interval)
    print("\n")